function gform_initialize_tooltips(){jQuery(".gf_tooltip").tooltip({show:500,content:function(){return jQuery(this).prop("title")},open:function(t,o){if(void 0===t.originalEvent)return!1;var i=jQuery(o.tooltip).attr("id");jQuery("div.ui-tooltip").not("#"+i).remove()},close:function(t,o){o.tooltip.hover(function(){jQuery(this).stop(!0).fadeTo(400,1)},function(){jQuery(this).fadeOut("500",function(){jQuery(this).remove()})})}})}jQuery(document).ready(function(){gform_initialize_tooltips()});